function client_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('client');
	if(!$arrPerm[1]){
		return false;
	}

	$data['nom_du_client'] = makeSafe($_REQUEST['nom_du_client']);
		if($data['nom_du_client'] == empty_lookup_value){ $data['nom_du_client'] = ''; }
	$data['nom_du_contact'] = makeSafe($_REQUEST['nom_du_contact']);
		if($data['nom_du_contact'] == empty_lookup_value){ $data['nom_du_contact'] = ''; }
	$data['titre_du_contact'] = makeSafe($_REQUEST['titre_du_contact']);
		if($data['titre_du_contact'] == empty_lookup_value){ $data['titre_du_contact'] = ''; }
	$data['adresse_postale'] = br2nl(makeSafe($_REQUEST['adresse_postale']));
	$data['ville'] = makeSafe($_REQUEST['ville']);
		if($data['ville'] == empty_lookup_value){ $data['ville'] = ''; }
	$data['code_postal'] = makeSafe($_REQUEST['code_postal']);
		if($data['code_postal'] == empty_lookup_value){ $data['code_postal'] = ''; }
	$data['adresse_gm'] = br2nl(makeSafe($_REQUEST['adresse_gm']));
	$data['pays'] = makeSafe($_REQUEST['pays']);
		if($data['pays'] == empty_lookup_value){ $data['pays'] = ''; }
	$data['telephone_contact'] = makeSafe($_REQUEST['telephone_contact']);
		if($data['telephone_contact'] == empty_lookup_value){ $data['telephone_contact'] = ''; }
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	$data['commentaires'] = makeSafe($_REQUEST['commentaires']);
		if($data['commentaires'] == empty_lookup_value){ $data['commentaires'] = ''; }
	if($data['nom_du_client']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Nom du client': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: client_before_insert
	if(function_exists('client_before_insert')){
		$args=array();
		if(!client_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `client` set       `nom_du_client`=' . (($data['nom_du_client'] !== '' && $data['nom_du_client'] !== NULL) ? "'{$data['nom_du_client']}'" : 'NULL') . ', `nom_du_contact`=' . (($data['nom_du_contact'] !== '' && $data['nom_du_contact'] !== NULL) ? "'{$data['nom_du_contact']}'" : 'NULL') . ', `titre_du_contact`=' . (($data['titre_du_contact'] !== '' && $data['titre_du_contact'] !== NULL) ? "'{$data['titre_du_contact']}'" : 'NULL') . ', `adresse_postale`=' . (($data['adresse_postale'] !== '' && $data['adresse_postale'] !== NULL) ? "'{$data['adresse_postale']}'" : 'NULL') . ', `ville`=' . (($data['ville'] !== '' && $data['ville'] !== NULL) ? "'{$data['ville']}'" : 'NULL') . ', `code_postal`=' . (($data['code_postal'] !== '' && $data['code_postal'] !== NULL) ? "'{$data['code_postal']}'" : 'NULL') . ', `adresse_gm`=' . (($data['adresse_gm'] !== '' && $data['adresse_gm'] !== NULL) ? "'{$data['adresse_gm']}'" : 'NULL') . ', `pays`=' . (($data['pays'] !== '' && $data['pays'] !== NULL) ? "'{$data['pays']}'" : 'NULL') . ', `telephone_contact`=' . (($data['telephone_contact'] !== '' && $data['telephone_contact'] !== NULL) ? "'{$data['telephone_contact']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `commentaires`=' . (($data['commentaires'] !== '' && $data['commentaires'] !== NULL) ? "'{$data['commentaires']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"client_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: client_after_insert
	if(function_exists('client_after_insert')){
		$res = sql("select * from `client` where `id_client`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!client_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='client', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

function client_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('client');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='client' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='client' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: client_before_delete
	if(function_exists('client_before_delete')){
		$args=array();
		if(!client_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: missions
	$res = sql("select `id_client` from `client` where `id_client`='$selected_id'", $eo);
	$id_client = db_fetch_row($res);
	$rires = sql("select count(1) from `missions` where `client`='".addslashes($id_client[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "missions", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "missions", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='client_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='client_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `client` where `id_client`='$selected_id'", $eo);

	// hook: client_after_delete
	if(function_exists('client_after_delete')){
		$args=array();
		client_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='client' and pkValue='$selected_id'", $eo);
}

