	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#nom_du_client').replaceWith('<div class=\"form-control-static\" id=\"nom_du_client\">' + (jQuery('#nom_du_client').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#nom_du_contact').replaceWith('<div class=\"form-control-static\" id=\"nom_du_contact\">' + (jQuery('#nom_du_contact').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#titre_du_contact').replaceWith('<div class=\"form-control-static\" id=\"titre_du_contact\">' + (jQuery('#titre_du_contact').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#adresse_postale').replaceWith('<div class=\"form-control-static\" id=\"adresse_postale\">' + (jQuery('#adresse_postale').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#ville').replaceWith('<div class=\"form-control-static\" id=\"ville\">' + (jQuery('#ville').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#code_postal').replaceWith('<div class=\"form-control-static\" id=\"code_postal\">' + (jQuery('#code_postal').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#adresse_gm').replaceWith('');\n";
		$jsReadOnly .= "\tjQuery('#adresse_gm, #adresse_gm-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#pays').replaceWith('<div class=\"form-control-static\" id=\"pays\">' + (jQuery('#pays').val() || '') + '</div>'); jQuery('#pays-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#telephone_contact').replaceWith('<div class=\"form-control-static\" id=\"telephone_contact\">' + (jQuery('#telephone_contact').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email').replaceWith('<div class=\"form-control-static\" id=\"email\">' + (jQuery('#email').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#email, #email-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode=str_replace('<%%COMBO(pays)%%>', $combo_pays->HTML, $templateCode);
	$templateCode=str_replace('<%%COMBOTEXT(pays)%%>', $combo_pays->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array();
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode=str_replace('<%%UPLOADFILE(id_client)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(nom_du_client)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(nom_du_contact)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(titre_du_contact)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(adresse_postale)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(ville)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(code_postal)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(adresse_gm)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(pays)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(telephone_contact)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(email)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(commentaires)%%>', '', $templateCode);

	// process values
	if($selected_id){
		$templateCode=str_replace('<%%VALUE(id_client)%%>', html_attr($row['id_client']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(id_client)%%>', urlencode($urow['id_client']), $templateCode);
		$templateCode=str_replace('<%%VALUE(nom_du_client)%%>', html_attr($row['nom_du_client']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(nom_du_client)%%>', urlencode($urow['nom_du_client']), $templateCode);
		$templateCode=str_replace('<%%VALUE(nom_du_contact)%%>', html_attr($row['nom_du_contact']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(nom_du_contact)%%>', urlencode($urow['nom_du_contact']), $templateCode);
		$templateCode=str_replace('<%%VALUE(titre_du_contact)%%>', html_attr($row['titre_du_contact']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(titre_du_contact)%%>', urlencode($urow['titre_du_contact']), $templateCode);
		if($dvprint){
			$templateCode = str_replace('<%%VALUE(adresse_postale)%%>', nl2br(html_attr($row['adresse_postale'])), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(adresse_postale)%%>', html_attr($row['adresse_postale']), $templateCode);
		}
		$templateCode=str_replace('<%%URLVALUE(adresse_postale)%%>', urlencode($urow['adresse_postale']), $templateCode);
		$templateCode=str_replace('<%%VALUE(ville)%%>', html_attr($row['ville']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(ville)%%>', urlencode($urow['ville']), $templateCode);
		$templateCode=str_replace('<%%VALUE(code_postal)%%>', html_attr($row['code_postal']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(code_postal)%%>', urlencode($urow['code_postal']), $templateCode);
		if($dvprint){
			$templateCode = str_replace('<%%VALUE(adresse_gm)%%>', nl2br(html_attr($row['adresse_gm'])), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(adresse_gm)%%>', html_attr($row['adresse_gm']), $templateCode);
		}
		$templateCode=str_replace('<%%URLVALUE(adresse_gm)%%>', urlencode($urow['adresse_gm']), $templateCode);
		$templateCode=str_replace('<%%VALUE(pays)%%>', html_attr($row['pays']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(pays)%%>', urlencode($urow['pays']), $templateCode);
		$templateCode=str_replace('<%%VALUE(telephone_contact)%%>', html_attr($row['telephone_contact']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(telephone_contact)%%>', urlencode($urow['telephone_contact']), $templateCode);
		$templateCode=str_replace('<%%VALUE(email)%%>', html_attr($row['email']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(email)%%>', urlencode($urow['email']), $templateCode);
		if($AllowUpdate || $AllowInsert){
			$templateCode = str_replace('<%%HTMLAREA(commentaires)%%>', '<textarea name="commentaires" id="commentaires" rows="5">' . html_attr($row['commentaires']) . '</textarea>', $templateCode);
		}else{
			$templateCode = str_replace('<%%HTMLAREA(commentaires)%%>', '<div id="commentaires" class="form-control-static">' . $row['commentaires'] . '</div>', $templateCode);
		}
		$templateCode=str_replace('<%%VALUE(commentaires)%%>', nl2br($row['commentaires']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(commentaires)%%>', urlencode($urow['commentaires']), $templateCode);
	}else{
		$templateCode=str_replace('<%%VALUE(id_client)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(id_client)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(nom_du_client)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(nom_du_client)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(nom_du_contact)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(nom_du_contact)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(titre_du_contact)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(titre_du_contact)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(adresse_postale)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(adresse_postale)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(ville)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(ville)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(code_postal)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(code_postal)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(adresse_gm)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(adresse_gm)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%GOOGLEMAP(adresse_gm)%%>', '', $templateCode);
		$templateCode=str_replace('<%%GOOGLEMAPTHUMB(adresse_gm)%%>', 'blank.gif', $templateCode);
		$templateCode=str_replace('<%%VALUE(pays)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(pays)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(telephone_contact)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(telephone_contact)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(email)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(email)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%HTMLAREA(commentaires)%%>', '<textarea name="commentaires" id="commentaires" rows="5"></textarea>', $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode=str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode=str_replace('<%%', '<!-- ', $templateCode);
	$templateCode=str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
			$templateCode.="\n\tif(document.getElementById('emailEdit')){ document.getElementById('emailEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('emailEditLink')){ document.getElementById('emailEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('client');
	if($selected_id){
		$jdata = get_joined_record('client', $selected_id);
		$rdata = $row;
	}
	$cache_data = array(
		'rdata' => array_map('nl2br', array_map('addslashes', $rdata)),
		'jdata' => array_map('nl2br', array_map('addslashes', $jdata)),
	);
	$templateCode .= loadView('client-ajax-cache', $cache_data);

	// hook: client_dv
	if(function_exists('client_dv')){
		$args=array();
		client_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>