function consultant_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('consultant');
	if(!$arrPerm[1]){
		return false;
	}

	$data['Matricule'] = makeSafe($_REQUEST['Matricule']);
		if($data['Matricule'] == empty_lookup_value){ $data['Matricule'] = ''; }
	$data['Prenom'] = makeSafe($_REQUEST['Prenom']);
		if($data['Prenom'] == empty_lookup_value){ $data['Prenom'] = ''; }
	$data['Nom'] = makeSafe($_REQUEST['Nom']);
		if($data['Nom'] == empty_lookup_value){ $data['Nom'] = ''; }
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	$data['adresse_postale'] = br2nl(makeSafe($_REQUEST['adresse_postale']));
	$data['saisie_par'] = parseCode('<%%creatorUsername%%>', true);
	$data['coache_par'] = makeSafe($_REQUEST['coache_par']);
		if($data['coache_par'] == empty_lookup_value){ $data['coache_par'] = ''; }
	$data['emploi_fonctionnel'] = makeSafe($_REQUEST['emploi_fonctionnel']);
		if($data['emploi_fonctionnel'] == empty_lookup_value){ $data['emploi_fonctionnel'] = ''; }
	if($data['Matricule']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Matricule': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['Nom']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Nom': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: consultant_before_insert
	if(function_exists('consultant_before_insert')){
		$args=array();
		if(!consultant_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `consultant` set       `Matricule`=' . (($data['Matricule'] !== '' && $data['Matricule'] !== NULL) ? "'{$data['Matricule']}'" : 'NULL') . ', `Prenom`=' . (($data['Prenom'] !== '' && $data['Prenom'] !== NULL) ? "'{$data['Prenom']}'" : 'NULL') . ', `Nom`=' . (($data['Nom'] !== '' && $data['Nom'] !== NULL) ? "'{$data['Nom']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `adresse_postale`=' . (($data['adresse_postale'] !== '' && $data['adresse_postale'] !== NULL) ? "'{$data['adresse_postale']}'" : 'NULL') . ', `saisie_par`=' . "'{$data['saisie_par']}'" . ', `coache_par`=' . (($data['coache_par'] !== '' && $data['coache_par'] !== NULL) ? "'{$data['coache_par']}'" : 'NULL') . ', `emploi_fonctionnel`=' . (($data['emploi_fonctionnel'] !== '' && $data['emploi_fonctionnel'] !== NULL) ? "'{$data['emploi_fonctionnel']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"consultant_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: consultant_after_insert
	if(function_exists('consultant_after_insert')){
		$res = sql("select * from `consultant` where `id_consultant`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!consultant_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('consultant', $recID, getLoggedMemberID());

	return $recID;
}

