function consultant_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('consultant');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='consultant' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='consultant' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['Matricule'] = makeSafe($_REQUEST['Matricule']);
		if($data['Matricule'] == empty_lookup_value){ $data['Matricule'] = ''; }
	if($data['Matricule']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Matricule': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['Prenom'] = makeSafe($_REQUEST['Prenom']);
		if($data['Prenom'] == empty_lookup_value){ $data['Prenom'] = ''; }
	$data['Nom'] = makeSafe($_REQUEST['Nom']);
		if($data['Nom'] == empty_lookup_value){ $data['Nom'] = ''; }
	if($data['Nom']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nom': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	$data['adresse_postale'] = br2nl(makeSafe($_REQUEST['adresse_postale']));
	$data['coache_par'] = makeSafe($_REQUEST['coache_par']);
		if($data['coache_par'] == empty_lookup_value){ $data['coache_par'] = ''; }
	$data['emploi_fonctionnel'] = makeSafe($_REQUEST['emploi_fonctionnel']);
		if($data['emploi_fonctionnel'] == empty_lookup_value){ $data['emploi_fonctionnel'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: consultant_before_update
	if(function_exists('consultant_before_update')){
		$args=array();
		if(!consultant_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `consultant` set       `Matricule`=' . (($data['Matricule'] !== '' && $data['Matricule'] !== NULL) ? "'{$data['Matricule']}'" : 'NULL') . ', `Prenom`=' . (($data['Prenom'] !== '' && $data['Prenom'] !== NULL) ? "'{$data['Prenom']}'" : 'NULL') . ', `Nom`=' . (($data['Nom'] !== '' && $data['Nom'] !== NULL) ? "'{$data['Nom']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `adresse_postale`=' . (($data['adresse_postale'] !== '' && $data['adresse_postale'] !== NULL) ? "'{$data['adresse_postale']}'" : 'NULL') . ', `coache_par`=' . (($data['coache_par'] !== '' && $data['coache_par'] !== NULL) ? "'{$data['coache_par']}'" : 'NULL') . ', `emploi_fonctionnel`=' . (($data['emploi_fonctionnel'] !== '' && $data['emploi_fonctionnel'] !== NULL) ? "'{$data['emploi_fonctionnel']}'" : 'NULL') . " where `id_consultant`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="consultant_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: consultant_after_update
	if(function_exists('consultant_after_update')){
		$res = sql("SELECT * FROM `consultant` WHERE `id_consultant`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id_consultant'];
		$args = array();
		if(!consultant_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='consultant' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function consultant_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('consultant');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	$AllowInsert = ($arrPerm[1] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}

	$filterer_coache_par = thisOr(undo_magic_quotes($_REQUEST['filterer_coache_par']), '');
	$filterer_emploi_fonctionnel = thisOr(undo_magic_quotes($_REQUEST['filterer_emploi_fonctionnel']), '');

	// populate filterers, starting from children to grand-parents

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: coache_par
	$combo_coache_par = new DataCombo;
	// combobox: emploi_fonctionnel
	$combo_emploi_fonctionnel = new DataCombo;

	if($selected_id){
		// mm: check member permissions
		if(!$arrPerm[2]){
			return "";
		}
		// mm: who is the owner?
		$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='consultant' and pkValue='".makeSafe($selected_id)."'");
		$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='consultant' and pkValue='".makeSafe($selected_id)."'");
		if($arrPerm[2]==1 && getLoggedMemberID()!=$ownerMemberID){
			return "";
		}
		if($arrPerm[2]==2 && getLoggedGroupID()!=$ownerGroupID){
			return "";
		}

		// can edit?
		if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){
			$AllowUpdate=1;
		}else{
			$AllowUpdate=0;
		}

		$res = sql("select * from `consultant` where `id_consultant`='".makeSafe($selected_id)."'", $eo);
		if(!($row = db_fetch_array($res))){
			return error_message($Translation['No records found'], 'consultant_view.php', false);
		}
		$urow = $row; /* unsanitized data */
		$hc = new CI_Input();
		$row = $hc->xss_clean($row); /* sanitize data */
		$combo_coache_par->SelectedData = $row['coache_par'];
		$combo_emploi_fonctionnel->SelectedData = $row['emploi_fonctionnel'];
